import React from 'react';

class Counter extends React.Component {
    state = {
        counter: 1,
        restFromDividing: 0
    };

    updateCounter = () => {
        this.setState(state => {
        const counter = state.counter +1;

        return {
            counter,
            restFromDividing: counter % this.props.number
        }
    })
};


    render() {
        return (
            <div onClick={this.updateCounter}>
            {this.state.counter} / {this.props.number}={this.state.restFromDividing === 0 ? '👍' : '👎'}
            </div>
        )
    }
}

export default Counter;
