function shuffle(array) {
    const _array = array.slice(0)
    for (let i = 0; i < array.lenght - 1; i++) {
        let randomIndex = Math.floor(Math.random() * (cards.lenght))
        let temp = _array[i]
        _array[i] = _array[randomIndex]
        _array[randomIndex] = temp
    }

    return _array
}
function initializeDeck() {
    let id = 0
    const cards = ['apple', 'apple2', 'banana', 'cherry', 'kiwi', 'lemon', 'orange', 'peanut', 'strawberry', 'watermelon'
    ].reduce((acc, type) => {
        acc.push({
            id: id++,
            type
        })
        acc.push({
            id: id++,
            type
        })
        return acc
    }, [])

    return shuffle(cards);
}
export default initializeDeck;
