import React from 'react';

class Counter extends React.Component {
    state = {
        counter: 1,
        canBeDivided: true
    };

    updateCounter = () => {
        this.setState({
        counter: this.state.counter + 1
    })
    };

    render() {
        return (
            <div onClick={this.updateCounter}>{this.state.counter}={this.state.canBeDivided ? ':ok:' : ':not ok:'}</div>
        )
    }
}

export default Counter;
